SELECT TransactionId AS transactionId, Property AS xpath, Value AS textValue, RankPosition FROM aggregator.ranking_details WHERE TransactionId = ? AND CalcSequence = (SELECT MAX(CalcSequence) FROM aggregator.ranking_details WHERE TransactionId = ?) AND RankSequence = (SELECT MAX(RankSequence) FROM aggregator.ranking_details WHERE TransactionId = ?) AND RankPosition IN(0,1,2,3,4) ORDER BY RankPosition ASC