INSERT INTO `ctm`.`coupons` (`couponId`, `styleCodeId`, `verticalId`, `campaignName`, `couponCode`, `couponValue`, `isExclusive`, `showPopup`, `canPrePopulate`, `contentBanner`, `contentSuccess`, `contentConfirmation`, `effectiveStart`, `effectiveEnd`, `couponChannel`, `removeFromLeads`, `vdn`) VALUES ('20', '1', '4', 'Direct Mail $100', '$100-offer', '100', '1', '1', '1', '<a href=\"javascript:;\" class=\"coupon-banner\" data-toggle=\"dialog\" data-content=\"#coupon-modal-content\" data-dialog-hash-id=\"coupon-more-info\" data-class=\"coupon-more-info-modal\"><span class=\"link\">Click here</span> to learn more about the $100 eftpos card offer.</a><div class=\"hidden\" id=\"coupon-modal-content\"><div class=\"row\"><div class=\"col-sm-5 col-xs-12\"><div class=\"coupon-modal-image health100eftpos\"></div></div><div class=\"col-sm-7 col-xs-12\"><h4>Get a $100 eftpos card when you call us or buy online</h4><p>Buy a hospital and extras policy, and we\'ll give you a $100 eftpos card. Just mention this offer to our Health Insurance Specialist on your call, or use the coupon code provided when purchasing online. Promotion ends 16 August 2015.</p><p><h4>Call <span class="noWrap callCentreNumber">1800 608 932</span></h4></p><p>View <a href="http://www.comparethemarket.com.au/competition/100eftpos-pdm-july.pdf" target="_blank">terms and conditions</a>. Frank, GMHBA and Budget Direct policies are not available through the call centre and are excluded from offer.</p></div></div></div>', '<div class=\"col-lg-9 col-sm-8 col-xs-12 col-lg-offset-3 col-sm-offset-4 row-content\"><span class=\"conpon-congrats\">Congratulations!</span> You\'ve entered a promo code for a $100 eftpos card. <a href=\"javascript:;\" data-toggle=\"dialog\" data-content=\"#coupon-modal-content-payment\" data-dialog-hash-id=\"coupon-more-info\" data-class=\"coupon-more-info-modal\">Find out more</a><div class=\"hidden\" id=\"coupon-modal-content-payment\"><div class=\"row\"><div class=\"col-sm-5 col-xs-12\"><div class=\"coupon-modal-image health100eftpos\"></div></div><div class=\"col-sm-7 col-xs-12\"><h4>Get a $100 eftpos card when you call us or buy online</h4><p>Buy a hospital and extras policy, and we\'ll give you a $100 eftpos card. Just mention this offer to our Health Insurance Specialist on your call, or use the coupon code provided when purchasing online. Promotion ends 16 August 2015.</p><p><h4>Call <span class="noWrap callCentreNumber">1800 608 932</span></h4></p><p>View <a href="http://www.comparethemarket.com.au/competition/100eftpos-pdm-july.pdf" target="_blank">terms and conditions</a>. Frank, GMHBA and Budget Direct policies are not available through the call centre and are excluded from offer.</p></div></div></div>', '<div class=\"coupon-confirmation\" data-coupon-id=\"20\"><h2>Congratulations!</h2><p>You\'re now eligible for a $100 eftpos card. You will receive an email in the next five days with instructions on how to claim.</p><div class=\"coupon-confirmation-image health100eftpos\"></div><p>View <a href=\"http://www.comparethemarket.com.au/competition/100eftpos-pdm-july.pdf\" target=\"_blank\">terms and conditions</a></p></div>', '2015-07-20 06:00:00', '2015-08-16 23:59:59', '', '0', '7436');
INSERT INTO `ctm`.`coupons` (`couponId`, `styleCodeId`, `verticalId`, `campaignName`, `couponCode`, `couponValue`, `isExclusive`, `showPopup`, `canPrePopulate`, `contentBanner`, `contentSuccess`, `contentConfirmation`, `effectiveStart`, `effectiveEnd`, `couponChannel`, `removeFromLeads`, `orderSeq`) VALUES ('21', '1', '4', 'Direct Mail $100 for split poilices', '$100eftpos-split', '100', '0', '0', '0', '<a href=\"javascript:;\" class=\"coupon-banner\" data-toggle=\"dialog\" data-content=\"#coupon-modal-content\" data-dialog-hash-id=\"coupon-more-info\" data-class=\"coupon-more-info-modal\"><span class=\"link\">Click here</span> to learn more about the $100 eftpos card offer.</a><div class=\"hidden\" id=\"coupon-modal-content\"><div class=\"row\"><div class=\"col-sm-5 col-xs-12\"><div class=\"coupon-modal-image health100eftpos\"></div></div><div class=\"col-sm-7 col-xs-12\"><h4>Get a $100 eftpos card when you call us or buy online</h4><p>Buy a hospital and extras policy, and we\'ll give you a $100 eftpos card. Just mention this offer to our Health Insurance Specialist on your call, or use the coupon code provided when purchasing online. Promotion ends 16 August 2015.</p><p>View <a href="http://www.comparethemarket.com.au/competition/100eftpos-pdm-july.pdf" target="_blank">terms and conditions</a>. Frank, GMHBA and Budget Direct policies are not available through the call centre and are excluded from offer.</p><p class="text-danger">Do not use unless customer mentions the $100 eftpos offer and is eligible. Apply this code if customer purchases a split policy (Hospital policy from Funda A and extras from Fund B) <br /><br />promo code: $100eftpos-split</p></div></div></div>', '<div class=\"col-lg-9 col-sm-8 col-xs-12 col-lg-offset-3 col-sm-offset-4 row-content\"><span class=\"conpon-congrats\">Congratulations!</span> You\'ve entered a promo code for a $100 eftpos card. <a href=\"javascript:;\" data-toggle=\"dialog\" data-content=\"#coupon-modal-content-payment\" data-dialog-hash-id=\"coupon-more-info\" data-class=\"coupon-more-info-modal\">Find out more</a><div class=\"hidden\" id=\"coupon-modal-content-payment\"><div class=\"row\"><div class=\"col-sm-5 col-xs-12\"><div class=\"coupon-modal-image health100eftpos\"></div></div><div class=\"col-sm-7 col-xs-12\"><h4>Get a $100 eftpos card when you call us or buy online</h4><p>Buy a hospital and extras policy, and we\'ll give you a $100 eftpos card. Just mention this offer to our Health Insurance Specialist on your call, or use the coupon code provided when purchasing online. Promotion ends 16 August 2015.</p><p>View <a href="http://www.comparethemarket.com.au/competition/100eftpos-pdm-july.pdf" target="_blank">terms and conditions</a>. Frank, GMHBA and Budget Direct policies are not available through the call centre and are excluded from offer.</p><p class="text-danger">Do not use unless customer mentions the $100 eftpos offer and is eligible. Apply this code if customer purchases a split policy (Hospital policy from Funda A and extras from Fund B) <br /><br />promo code: $100eftpos-split</p></div></div></div>', '<div class=\"coupon-confirmation\" data-coupon-id=\"21\"><h2>Congratulations!</h2><p>You\'re now eligible for a $100 eftpos card. You will receive an email in the next five days with instructions on how to claim.</p><div class=\"coupon-confirmation-image health100eftpos\"></div><p>View <a href=\"http://www.comparethemarket.com.au/competition/100eftpos-pdm-july.pdf\" target=\"_blank\">terms and conditions</a></p></div>', '2015-07-20 06:00:00', '2015-08-16 23:59:59', 'C', '0', '3');
